mapper.update:
UPDATE main_plan
departure_date = #{departureDate:DATE} , arrival_date = #{arrivalDate:DATE}, country = #{country:VARCHAR}, city = #{city:VARCHAR}, plan_title =#{planTitle:VARCHAR}, main_thumbnail =#{mainThumbnail:VARCHAR}
WHERE main_plan_no = #{mainPlanNo}

mapper.select:
SELECT * 
FROM(SELECT inner_table.*, ROWNUM AS row_seq
				FROM (SELECT * FROM main_plan
					WHERE user_no=#{user.userNo}
					ORDER BY main_plan_no ASC
					) inner_table
			WHERE ROWNUM <=#{endClick} ) 
		WHERE row_seq BETWEEN #{startClick} AND {endClick}
