mapper.update:
UPDATE product
product_title = #{productName} , product_price = #{productPrice}, product_type = #{productType} , trip_date = #{tripDate} , country = #{country} , city = #{city} , product_content = #{description:VARCHAR} , head_count = #{tourHeadCount}
WHERE product_no = #{productNo}

mapper.select:
  	SELECT *
  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
  					FROM		(	
								 ) inner_table
					WHERE ROWNUM <= #{search.endRowNum} )
	WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

mapper.sql:
	 SELECT product_no, product_title, product_price, product_type, country, city, view_count, user_no				   
	 FROM product		
	
		
			
 				AND country= #{search.searchKeyword}
			
			
 				AND city = #{search.searchKeyword}
			
			
 				AND product_title LIKE '${search.searchKeyword}%'
			
			
 				AND user_no LIKE '${search.searchKeyword}%'
			
			
				
 					AND product_price= #{search.searchKeyword}
 				
 				
 					AND product_price BETWEEN #{str[0]} AND #{str[1]}
 				
			
		
	
	
		
	ORDER BY reg_date DESC

mapper.update:
   	UPDATE product
   		   		
		view_count			= view_count+1						
   	
   	WHERE product_no = #{productNo}
