mapper.select:
	SELECT *
  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
  					FROM		(	SELECT 
								  	tran_no , tran_date , trip_date , requirement, pay_type , product_no, host_no, tran_count, product_price, user_no, tran_price 
								  	FROM transaction
								  	
									  	
						 					user_no= #{buyerNo}
						 				
						 				
						 					host_no= #{hostNo}
						 				
					 												  	 
								  	ORDER BY tran_date DESC
								 ) inner_table
					WHERE ROWNUM <= #{search.endRowNum} )
	WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

mapper.update:
UPDATE refund 
SET 
	refund_confirm_date=#{confirmDate} 
WHERE refund_no=#{value}

UPDATE transaction 
 
	product_star_eval	= #{starEvalProduct} ,
	product_review		= #{reviewProduct}

WHERE tran_no=#{tranNo}

mapper.select:
	SELECT *
  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
  					FROM		(	SELECT
  									
						 				product_no, tran_no , tran_date , trip_date , host_no, tran_count, user_no, product_star_eval, product_review
						 			
						 			
						 				host_no, tran_no , tran_date , trip_date , product_no, tran_count, user_no, host_star_eval, host_review
						 												  	
								  	FROM transaction
								  									  	
										
							 				product_no= #{productNo}
							 			
							 			
							 				host_no= #{hostNo}
							 			
						 								 												  	 
								  	ORDER BY tran_date DESC
								 ) inner_table
					WHERE ROWNUM <= #{search.endRowNum} )
	WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

mapper.update:
UPDATE transaction 
 
	host_star_eval	= #{starEvalHost} ,
	host_review		= #{reviewHost}

WHERE tran_no = #{value}

mapper.select:
SELECT
AVG(product_star_eval)
AVG(host_star_eval)		
FROM transaction

	product_no = #{productNo}
	host_no = #{hostNo}

  	SELECT COUNT(*)
  	FROM(	SELECT
  			
  				refund_no, tran_no, refund_reg_date, refund_confirm_date, refund_price, refund_account, refund_bank 
								  			FROM refund
				
  				tran_no , tran_date , trip_date , requirement, pay_type , product_no, host_no, tran_count, product_price, user_no, tran_price
						FROM transaction
				
  							 
			
				WHERE user_no= #{buyerNo}
			
			
				WHERE host_no= #{hostNo}
			
			ORDER BY tran_date DESC
		 ) countTable
